package string_concept;
public class CompEqualsMethod {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
String s1="neelu";
String s2="ramya";
String s3=new String("durga");
String s4="reddy";
System.out.println(s1.equals(s1));//comparing s1 with s1 
System.out.println(s1.equals(s2));//comparing s1 with s2
System.out.println(s1.equals(s3));//comparing s1 with s3
System.out.println(s1.equals(s4));//comparing s1 with s4
	}}
