package string_concept;
public class CompToMethod {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1="r";
		String s2="R";
		String s3=new String("U");
		String s4="U";
		System.out.println(s1.compareTo(s1));//comparing s1 with s1 
		System.out.println(s1.compareTo(s2));//comparing s1 with s2
		System.out.println(s2.compareTo(s3));//comparing s2 with s3
		System.out.println(s3.compareTo(s4));//comparing s3 with s4

	}}
