package string_concept;
public class DemoDoubleEqual {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1="durga";
		String s2="ramya";
		String s3=new String("durga");
		String s4="reddy";
		System.out.println(s1==s1);//comparing s1 with s1 
		System.out.println(s1==s2);//comparing s1 with s2
		System.out.println(s1==s3);//comparing s1 with s3
		System.out.println(s1==s4);//comparing s1 with s4

	}}
