package string_concept;

public class DemoString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String s="java";  //initialization of string obj==string literal method
char ch[]={'c','v','s','r'}; //array of characters
String p=new String(ch); //Conversion char array to string array
String q=new String("example"); //initialization string obj==new keyword method
System.out.println(s);
System.out.println(p);
System.out.println(q);
}}
